    "top_selected_stories": {
      "type": "array",
      "minItems": 4,
      "maxItems": 4,
      "description": "A list of four chosen stories (including the top/main story).",
      "items": {
        "type": "object",
        "properties": {
          "title": {
            "type": "string",
