          },
          "external_source_links": {
            "type": "array",
            "description": "The top-level sources (press releases, official blog posts, etc.) drawn directly from the provided text. Only include links if they appear in the raw content provided or in the external-source-urls provided.",
            "items": {
              "type": "string"
            }
          }
        },
        "required": [
          "title",
          "summary",
          "reason_for_selecting",
          "identifiers",
          "external_source_links"
        ]
      }
    }
  },
  "required": [
    "top_selected_stories_chain_of_thought",
    "top_selected_stories"
  ]
}

