parameters.jsCode:
let category_content = $input.all().map(o => {
  let result = "";

  result += `Title: ${o.json.title}\n`;
  result += `Description: ${o.json.meta_description}\n`;
  result += `Url: https://aitools.inc/categories/${o.json.slug}`;

  return result;
}).join("\n\n");

return { category_content };
